v8::Handle<v8::Value> thingface_js_dispatcher::v8query_interface(const v8::Arguments& args)
{
    if(args.Length() < 1)
        return v8::ThrowException(v8::String::New("interface creator name required"));

    v8::HandleScope _handle_scope_;
    v8::String::AsciiValue key(args[0]);
    
    typedef v8::Handle<v8::Value> (*fn_get)(const v8::Arguments&, interface_context*);
    coid::token tokey(*key, key.length());
    fn_get get = reinterpret_cast<fn_get>(
        coid::interface_register::get_interface_creator(tokey));

    if(!get) {
        coid::charstr tmp = "interface creator ";
        tmp << tokey << " not found";
        return v8::ThrowException(v8::String::New(tmp.c_str()));
    }
        
    v8::Local<v8::Object> _obj_ = args.Holder();

    v8::Local<v8::Value> _intobj_ = _obj_->GetInternalField(0);
    if(!_intobj_->IsExternal())
        return v8::Undefined();

    ifc1::ifc2::js::thingface_js_dispatcher* ifc = static_cast<ifc1::ifc2::js::thingface_js_dispatcher*>
        (v8::Handle<v8::External>::Cast(_intobj_)->Value());

    if(!ifc)
        return v8::ThrowException(v8::Exception::ReferenceError(v8::String::New("null interface object in $query_interface")));

    return _handle_scope_.Close(get(args, ifc));
}

